SELECT COUNT(*) FROM( SELECT BOARD_NO, CATEGORY_CODE, ANIMAL_CODE, ANIMAL_NAME, BOARD_STATUS, BO_CATEGORY_NAME, ROW_NUMBER() OVER(PARTITION BY BOARD_NO ORDER BY CATEGORY_CODE DESC ) AS rn FROM TB_INFO LEFT JOIN TB_BOARD_ANIMAL USING(BOARD_NO) LEFT JOIN TB_ANIMAL USING(ANIMAL_CODE) LEFT JOIN TB_BOARD_CATEGORY ON(BO_CATEGORY_CODE = CATEGORY_CODE) ) WHERE RN = 1 AND BOARD_STATUS = 'Y'
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND CATEGORY_CODE = #{category}
AND CATEGORY_CODE = #{category}
AND CATEGORY_CODE IN ('I1', 'I2')

