mapper.select:
SELECT BOARD_NO, BOARD_WRITER, ANIMAL_NAME, CREATE_DATE, BOARD_TITLE, BOARD_CONTENT, BOARD_COUNT, NVL(BOARD_LIKE, 0) BOARD_LIKE, SUM_COUNT FROM ( SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_COUNT, TB_MEMBER.NICKNAME BOARD_WRITER, BO_CATEGORY_NAME, BO_CATEGORY_CODE CATEGORY_CODE, ANIMAL_CODE, ANIMAL_NAME, TB_INFO.CREATE_DATE CREATE_DATE, NVL(LIKE_COUNT, 0) BOARD_LIKE, REPLY_COUNT + COMMENT_COUNT SUM_COUNT, BOARD_STATUS, ROW_NUMBER() OVER(PARTITION BY BOARD_NO ORDER BY CATEGORY_CODE DESC ) AS RN FROM TB_INFO LEFT JOIN TB_BOARD_ANIMAL USING(BOARD_NO) LEFT JOIN TB_ANIMAL USING(ANIMAL_CODE) LEFT JOIN TB_BOARD_CATEGORY ON(BO_CATEGORY_CODE = CATEGORY_CODE) LEFT JOIN TB_MEMBER USING(MEMBER_NO) LEFT JOIN ( SELECT COUNT(*) LIKE_COUNT, BOARD_NO FROM TB_LIKE GROUP BY BOARD_NO ) USING (BOARD_NO) LEFT JOIN ( SELECT BOARD_NO, COUNT(REPLY_NO) REPLY_COUNT, COUNT(COMMENT_NO) COMMENT_COUNT FROM TB_INFO LEFT JOIN TB_REPLY USING(BOARD_NO) LEFT JOIN TB_COMMENT USING(REPLY_NO) GROUP BY BOARD_NO ) USING (BOARD_NO)
LEFT JOIN TB_ATTACHMENT USING(BOARD_NO) ORDER BY BOARD_NO ) WHERE BOARD_STATUS = 'Y' AND RN = 1
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND ANIMAL_CODE = #{animal}
AND CATEGORY_CODE = #{category}
AND CATEGORY_CODE = #{category}
AND CATEGORY_CODE IN ('I1', 'I2')
